var add_item = function(conString,table,params,callback){
	var nano = require('nano')(conString);
	var db = nano.use(config.db.database_name);
	console.log(params);
	if(typeof params != 'object'){
		params = JSON.parse(params);
	}
	db.insert(params, params.id, function(err, body){
		if (!err) {
			callback(null, body);
		} else {
			console.log(err);
			callback(err,null);
		}
	});
	//callback('no value', null);
};

module.exports = add_item;